#, 'http://business8.geolook.at/apotheken/find.php?nd=1&ort=']
$i = 0
def fetchApo(plzA, baseUrlA)
  
  plzA.each do |plz|
    if $i 
     puts $i
    end
    baseUrl = baseUrlA[0]
    url = baseUrl + plz.to_s
    puts url
    html = ScraperWiki.scrape(url)
    doc = Nokogiri::HTML(html)
     $i = 0
    #/HTML[1]/BODY[1]/DIV[3]/TABLE[1]/TBODY[1]/TR[1]/TD[1]
    doc.xpath('//body/div/table/tr').each do |apotr|
      
      d = {}
      d['plz']=plz
      d['city']=nil
      d['name']=nil
      d['adr']=nil
      d['tel']=nil
      d['fax']=nil
      d['apodienst']=nil
      d['email']=nil
      d['requesttime']=Time.now
      
      ##puts apotr
      d['name']=apotr.search('span.aponame').text
      d['adr']=apotr.search('span.apoadr').text
      apocity = apotr.search('span.apocity').text
      nplz = Integer(apocity.gsub(/\D/, ''))
      
      if nplz && nplz == plz
      if nplz
          d['plz']=nplz    
      end
      plz = nplz
      #puts plz
      city=apocity.gsub(/[0-9]/, '').to_s
      city[0,2]=''
      #puts apocity
      d['city'] = city
      
      d['tel']=apotr.search('span.apotel').text.gsub(/Tel\.:/, '')
      d['fax']=apotr.search('span.apofax').text.gsub(/Fax\.:/, '')
      d['email']=apotr.search('span.apoemail a').text
      
 
      nachtdienst=apotr.search('span.apodiensttext').text.scan(/[0-9][0-9]\.[0-9][0-9]\.[0-9][0-9][0-9][0-9]/)   
      d['apodienst'] = nachtdienst[0]
     #d['apodienst']=nachtdienst[0]
      #apodata = Nokogiri::HTML(apotr)
      #if d['apodienst'] 
        #puts d['name']+' '+d['apodienst']+' '+d['city']+' '+d['plz'].to_s
     # end
      d.each do |k, v|
       if v.is_a? String
        d[k]=v.strip
       end
      end
      
      ScraperWiki.save(['plz','city','name', 'adr', 'tel', 'fax','email','apodienst', 'requesttime'], d)
      $i = $i+1
      end
    end
  
  end
  
  
end

